package 반복문;

public class ForTest { // file명 = class명

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 시작값, 조건식, 증감값
		for (int i = 0; i < 10; i++) { // default : int i = 0 // 조건식 : print조건 // (1)
			// 조건이 만족했을때, 비교연산자의 결과가 true! 일때
			System.out.println(i); // (2) // 0 부터 출력
			System.out.println(true); // (3)
		}

	}

}
